import React, { useState } from 'react';
import '../styles/chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Bonjour ! Je suis là pour vous aider ✨' },
  ]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim() === '') return;

    const newMessages = [...messages, { sender: 'user', text: input }];
    setMessages(newMessages);

    // Simule une réponse du bot
    setTimeout(() => {
      setMessages(prev => [...prev, { sender: 'bot', text: "Je réfléchis à votre demande..." }]);
    }, 1000);

    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="chatbot__container">
      <div className="chatbot__header">Assistant Virtuel</div>
      <div className="chatbot__messages">
        {messages.map((msg, idx) => (
          <div key={idx} className={`chatbot__message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chatbot__input">
        <input
          type="text"
          placeholder="Tapez votre message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button onClick={handleSend}>Envoyer</button>
      </div>
    </div>
  );
};

export default Chatbot;
