import React, { useState, useRef, useEffect } from 'react';
import { RiSendPlaneFill } from 'react-icons/ri';
import '../styles/chatbot.css';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { 
      sender: 'bot', 
      text: 'Bonjour ! Je suis votre assistant virtuel. Posez-moi vos questions.' 
    },
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  // Effet de scroll automatique
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (!input.trim()) return;
    
    // Ajout du message utilisateur
    setMessages(prev => [...prev, { sender: 'user', text: input }]);
    setInput('');
    
    // Réponse intelligente
    setTimeout(() => {
      const responses = [
        "J'ai bien noté votre demande.",
        "Je transfère à un conseiller spécialisé.",
        "Merci pour votre message. Nous traitons votre demande.",
        "Pouvez-vous préciser votre demande ?"
      ];
      setMessages(prev => [...prev, {
        sender: 'bot',
        text: responses[Math.floor(Math.random() * responses.length)]
      }]);
    }, 800);
  };

  return (
    <div className="chatbot__container">
      <div className="chatbot__header">
        <span>Assistance Premium</span>
      </div>
      
      <div className="chatbot__messages">
        {messages.map((msg, i) => (
          <div key={i} className={`chatbot__message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      
      <div className="chatbot__input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Écrivez votre message..."
        />
        <button onClick={handleSend}>
          <RiSendPlaneFill size={18} />
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
